import React, { useEffect, useState } from 'react'
import UpperHeading from '../../Components/UpperHeading'
import './Planets.css'

const Planets = () => {
    const [planets,setPlanets] = useState([])
    const[nextPage,setNextPage] = useState('')
    const[prevPage,setPrevPage] = useState('')
    useEffect(()=>{
        fetch('https://swapi.dev/api/planets/?format=json')
        .then(response => response.json())
        .then(data => {
            setPlanets(data.results);
        })
        .catch(error=>{
            console.error("Error fetching Data",error)
        })
    },[])

    const handlePrevPage = () => {
        if(prevPage){
            fetch(prevPage)
            .then(response => response.json())
            .then(data => {
                setPlanets(data.results)
                setNextPage(data.next)
                setPrevPage(data.pre)
            })
        }
    }


  return (
    <div>
      <UpperHeading/>
      <div className="container">
        <div className="row">
            {
                planets.map(planet => (
                    <div key={planet.name} className="col-lg-4 planet-button-div mt-5 mb-5">
                        <button className='planet-button'>{planet.name}</button>
                    </div>
                ))
            }
        </div>
        <div className="row">
            <div className="col-lg-6 m-auto">
                <div class="btn-group btn-group-lg button-group" role="group" aria-label="Large button group">
                    <button type="button" class="btn btn-outline-info" onClick={handlePrevPage}>Prev</button>
                    <button type="button" class="btn btn-outline-info" onClick={handleNextPage}>Next</button>
                </div>
            </div>
        </div>
      </div>
    </div>
  )
}

export default Planets
